package KafkaTesting.demo.Test.Consumer;import KafkaTesting.demo.Test.Model.UserEvent;import org.springframework.kafka.annotation.KafkaListener;import org.springframework.stereotype.Component;import java.util.Collections;import java.util.LinkedList;import java.util.List;@Componentpublic class KafkaConsumer {    private final List<UserEvent> eventLog =new LinkedList<>();    @KafkaListener(topics = "topic_Beni", groupId = "my-spring-boot-consumer-group")    public  void  consume(UserEvent event) {        System.out.println("Consumed: " + event);        if (eventLog.size() >= 100) {            eventLog.remove(0);        }        eventLog.add(event);    }    public List<UserEvent> getAllEvents() {        return new LinkedList<>(eventLog);    }}