package KafkaTesting.demo.Test.Consumer;import KafkaTesting.demo.Test.Model.UserEvent;import org.springframework.kafka.annotation.KafkaListener;import java.util.LinkedList;import java.util.List;public class WikiMediaConsumer {    private final List<String> incomingMessage =new LinkedList<>();    @KafkaListener(topics = "topic_2",groupId = "my-spring-boot-consumer-group")    void getMessage(List<String> messages) {        for (String message : messages) {            System.out.println("Received message: " + message);            incomingMessage.add(messages);        }    }}