package KafkaTesting.demo.Test.Service;import com.launchdarkly.eventsource.EventSource;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.core.env.Environment;import java.net.URI;import java.util.concurrent.TimeUnit;public class WikiMediaService {    @Autowired    Environment environment;    public void implementServiceLogic() throws InterruptedException {        WikiMediaChangesHandler wikiMediaChangesHandler = new WikiMediaChangesHandler();        String url = environment.getProperty("event.source.url");        final EventSource build = new EventSource.Builder(wikiMediaChangesHandler, URI.create(url)).build();        build.start();        TimeUnit.MINUTES.sleep(5);    }}